mapper.select:
    	select t.*,tt.name as creatorName from cdiscount_api_config t left join user tt on t.creator = tt.id
    	
    		
    			and t.id = #{params.id}
    		
    		
				
					and t.shopName like CONCAT('%','${params.shopName}','%')  
				
				
					and t.shopName = #{params.shopName}
				 		
    		
    		
				
					and t.apiAccount like CONCAT('%','${params.apiAccount}','%')  
				
				
					and t.apiAccount = #{params.apiAccount}
				 		
    		
    		
				
					and t.email like CONCAT('%','${params.email}','%')  
				
				
					and t.apiAccount = #{params.apiAccount}
				 		
    		
    		
				and t.closeStatus = #{params.closeStatus}  		
    		
    		
    			 and t.createDate >= #{params.createDateFrom} 
    		
    		
    			 and t.createDate <= #{params.createDateTo} 
    		
    		
    			 and t.lastUpdateTime >= #{params.lastUpdateTimeFrom} 
    		
    		
    			 and t.lastUpdateTime <= #{params.lastUpdateTimeTo} 
    		
    		
    		
    			and t.creator = #{params.userId}
    		
    	
    	order by t.id desc
