mapper.select:
    	select * from cdiscount_api_config
    	
    		
    			id = #{params.id}
    		
    		
				
					and shopName like CONCAT('%','${params.shopName}','%')  
				
				
					and shopName = #{params.shopName}
				 		
    		
    		
				
					and apiAccount like CONCAT('%','${params.apiAccount}','%')  
				
				
					and apiAccount = #{params.apiAccount}
				 		
    		
    		
				and closeStatus = #{params.closeStatus}  		
    		
    	
    	 order by id desc
