mapper.select:
		select t.*, tt.shopName from cdiscount_ean t 
			left join cdiscount_api_config tt on t.apiId = tt.id 
		
			
    			t.creator = #{params.userId}

mapper.update:
update cdiscount_ean set
	apiId = #{apiId},
	sku = #{sku},
	isUsed = #{isUsed},
	usedTime = #{usedTime}
where id = #{id}
